mapper.select:
/* ArticleMapper.isArticleAuthor */
SELECT
    COUNT(*)
FROM
    ARTICLE
WHERE
    `article_no` = #{articleNo}
    AND `writer_no` = #{writerNo}

mapper.update:
UPDATE ARTICLE
SET view_cnt = view_cnt + 1
WHERE `article_no`=#{articleNo}

mapper.insert:
INSERT INTO ARTICLE_LIKE (
     article_no
    , reg_no
    , reg_date
) VALUES (
          #{articleNo}
         , #{regNo}
         , NOW()
)

mapper.select:
SELECT like_no
FROM ARTICLE_LIKE
WHERE article_no = #{articleNo}
  AND reg_no = #{regNo}

mapper.insert:
/* ArticleMapper.insertMember */
INSERT INTO ARTICLE (
                      article_no
                    , image_url
                    , title
                    , content
                    , writer_no
                    , view_cnt
                    , like_cnt
                    , delete_yn
                    , reg_date
                    , update_date
) VALUES (
           #{articleNo}
         , #{imageUrl}
         , #{title}
         , #{content}
         , #{writerNo}
         , #{viewCnt}
         , #{likeCnt}
         , "N"
         , NOW()
         , NULL
         )
