mapper.select:
/* ArticleMapper.getArticleList */ SELECT A.article_no , A.image_url , A.title , A.sub_title , A.content , A.writer_no , M.mbr_id , A.view_cnt , A.like_cnt , A.delete_yn , A.reg_date , A.update_date FROM ARTICLE A LEFT JOIN tb_member M ON A.writer_no = M.mbr_no WHERE A.delete_yn = 'N'
AND ( title like CONCAT ('%', #{keyword}, '%') /* OR sub_title like CONCAT ('%', #{keyword}, '%') OR content like CONCAT ('%', #{keyword}, '%') )*/
LIMIT #{pageable.offset}, #{pageable.pageSize}

/* ArticleMapper.isArticleAuthor */
SELECT
    COUNT(*)
FROM
    ARTICLE
WHERE
    `article_no` = #{articleNo}
    AND `writer_no` = #{writerNo}

mapper.update:
UPDATE ARTICLE
SET view_cnt = view_cnt + 1
WHERE `article_no`=#{articleNo}

mapper.insert:
INSERT INTO ARTICLE_LIKE (
     article_no
    , reg_no
    , reg_date
) VALUES (
          #{articleNo}
         , #{regNo}
         , NOW()
)

mapper.select:
SELECT like_no
FROM ARTICLE_LIKE
WHERE article_no = #{articleNo}
  AND reg_no = #{regNo}

mapper.insert:
/* ArticleMapper.insertMember */
INSERT INTO ARTICLE (
                      article_no
                    , image_url
                    , title
                    , content
                    , writer_no
                    , view_cnt
                    , like_cnt
                    , delete_yn
                    , reg_date
                    , update_date
) VALUES (
           #{articleNo}
         , #{imageUrl}
         , #{title}
         , #{content}
         , #{writerNo}
         , #{viewCnt}
         , #{likeCnt}
         , "N"
         , NOW()
         , NULL
         )
